import { PlusOutlined, QuestionCircleOutlined } from '@ant-design/icons';
import {
  Button,
  message,
  Input,
  Drawer,
  List,
  Avatar,
  Skeleton,
  Divider,
  Row,
  Col,
  Tooltip,
  Image,
} from 'antd';
import React, { useState, useRef, useEffect } from 'react';
import styles from '../index.less';
import DividerComponent from './Divider';

import InfiniteScroll from 'react-infinite-scroll-component';
// import List from 'rc-virtual-list';
/**
 * @en-US Add node
 * @zh-CN 添加节点
 * @param fields
 */

const BlogHotList = () => {
  /**
   * @en-US International configuration
   * @zh-CN 国际化配置
   * */
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState([]);

  const loadMoreData = () => {
    if (loading) {
      return;
    }
    setLoading(true);
    fetch('http://localhost:3000/results')
      .then((res) => res.json())
      .then((body) => {
        setData([...data, ...body]);
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
      });
  };

  useEffect(() => {
    loadMoreData();
  }, []);

  return (
    <Col className="gutter-row" span={18} style={{ marginTop: '-20px' }}>
      <DividerComponent />
      <InfiniteScroll
        dataLength={data.length}
        next={loadMoreData}
        hasMore={data.length < 100}
        loader={<Skeleton avatar paragraph={{ rows: 1 }} active />}
        endMessage={<Divider plain>榜单只展示前一百名哦！！！🤐</Divider>}
        scrollableTarget="scrollableDiv"
      >
        <List
          itemLayout="horizontal"
          dataSource={data}
          renderItem={(item, index) => (
            <List.Item className={styles.rankItem}>
              <text
                className={
                  index == 0
                    ? styles.rankFirst
                    : index == 1
                    ? styles.rankSecond
                    : index == 2
                    ? styles.rankThird
                    : styles.rankCommon
                }
              >
                {index + 1}
              </text>
              <List.Item.Meta
                // avatar={
                //   <Image
                //     style={{ borderRadius: '4px' }}
                //     width={80}
                //     height={55}
                //     src={item.picture.large}
                //     fallback="data:image/png;base64,"
                //   />
                // }
                title={
                  <a href="https://ant.design" style={{ fontSize: '18px' }}>
                    {item.title}
                  </a>
                }
                description={
                  <text>浏览 12323&nbsp;&nbsp;&nbsp; 评论 234 &nbsp;&nbsp;&nbsp;收藏 133</text>
                }
              />
              <Button
                type="link"
                shape="round"
                size="middle"
                style={{
                  marginRight: '30px',
                  borderWidth: '1px',
                  // height: '30px',
                  borderColor: '#555666',
                  color: '#555666',
                }}
              >
                关注blog
              </Button>
            </List.Item>
          )}
        />
      </InfiniteScroll>
    </Col>
  );
};

export default BlogHotList;
